import React, { useState } from 'react';
import styled from 'styled-components';
import ImageGallery from './ImageGallery.jsx';
import SearchBar from './SearchBar.jsx';

const CardWrapper = styled.div`
  background: white;
  border-radius: 12px;
  width: 320px;
  height: 600px;
  display: flex;
  flex-direction: column;
  overflow: hidden;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
`;

const CardHeader = styled.div`
  padding: 16px;
  border-bottom: 1px solid #f0f0f0;
`;

const CardTitle = styled.h3`
  margin: 0 0 8px 0;
  font-size: 16px;
  font-weight: 600;
  color: #333;
`;

const CardContent = styled.div`
  flex: 1;
  padding: 16px;
  display: flex;
  flex-direction: column;
`;

const ContentText = styled.p`
  font-size: 14px;
  color: #666;
  line-height: 1.5;
  margin: 0 0 20px 0;
`;

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const Modal = styled.div`
  background: #4a4a4a;
  padding: 30px;
  border-radius: 16px;
  text-align: center;
  color: white;
  max-width: 280px;
`;

const ModalIcon = styled.div`
  width: 60px;
  height: 60px;
  background: #5a9fd4;
  border-radius: 50%;
  margin: 0 auto 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 24px;
`;

const ModalText = styled.div`
  margin-bottom: 20px;

  .main-text {
    font-size: 16px;
    margin-bottom: 8px;
  }

  .sub-text {
    font-size: 14px;
    color: #ccc;
  }
`;

const ModalButtons = styled.div`
  display: flex;
  flex-direction: column;
  gap: 8px;

  button {
    padding: 12px;
    border: none;
    border-radius: 8px;
    font-size: 14px;
    cursor: pointer;

    &.primary {
      background: #5a9fd4;
      color: white;
    }

    &.secondary {
      background: transparent;
      color: #ccc;
      border: 1px solid #666;
    }
  }
`;

const Card = ({ data, onSearchFocus }) => {
  const [showModal, setShowModal] = useState(false);

  const handleModalOpen = () => {
    if (data.hasModal) {
      setShowModal(true);
    }
  };

  return (
    <>
      <CardWrapper>
        <CardHeader>
          <CardTitle>{data.title}</CardTitle>
        </CardHeader>

        <CardContent>
          <ContentText>{data.content}</ContentText>

          {data.hasImages ? (
            <ImageGallery />
          ) : (
            <div onClick={handleModalOpen} style={{ cursor: 'pointer' }}>
              {/* Îπà Í≥µÍ∞Ñ ÎòêÎäî Îã§Î•∏ Ïª®ÌÖêÏ∏† */}
            </div>
          )}

          <SearchBar onFocus={onSearchFocus} />
