      </CardWrapper>

      {showModal && (
        <ModalOverlay onClick={() => setShowModal(false)}>
          <Modal onClick={e => e.stopPropagation()}>
            <ModalIcon>üì±</ModalIcon>
            <ModalText>
              <div className="main-text">
                Ï†ëÍ∑ºÏùÑ ÌóàÏö©ÌïòÏãúÍ≤†ÏäµÎãàÍπå?
              </div>
              <div className="sub-text">
                ÏÇ¨ÏßÑ Î∞è ÎèôÏòÅÏÉÅ Ï†ëÍ∑º
              </div>
            </ModalText>
            <ModalButtons>
              <button className="primary">ÌôïÏù∏ÌïòÏÑ∏Ïöî</button>
              <button className="secondary">Ï∑®ÏÜåÌïòÍ∏∞</button>
            </ModalButtons>
          </Modal>
        </ModalOverlay>
      )}
    </>
  );
};

export default Card;
